import React,{useState} from "react";





function CreateArea() {
  var [item, setItem] = useState({
    title:"",
    content:""
  });
  function getItem(event){
    var name = event.target.name;
    var value = event.target.value;

      setItem(
        prevItem => {
          return {
            ...prevItem,
            [name] : value
          } ;
        }
      )
      console.log(item);
  }

  return (
    <div>
      <form>
        <input onChange= {getItem}  name="title" placeholder="Title" />
        <textarea onChange = {getItem} name="content" placeholder="Take a note..." rows="3" />
        <button>Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
