import React,{useState} from "react";
import AddCircleIcon from '@material-ui/icons/AddCircle';




function CreateArea(props) {
  var [item, setItem] = useState({
    title:"",
    content:""
  });
  function getItem(event){
    var name = event.target.name;
    var value = event.target.value;

      setItem(
        prevItem => {
          return {
            ...prevItem,
            [name] : value
          } ;
        }
      )
  }

  function preventDefault(event){
    event.preventDefault();
  }

  return (
    <div>
      <form className = "create-note" onSubmit = {preventDefault}>
        <input onChange= {getItem}  name="title" placeholder="Title" value={item.title}/>
        <textarea onChange = {getItem} name="content" placeholder="Take a note..." rows="3" value = {item.content} />
        <button onClick = {()=> props.click(item)}>
          <AddCircleIcon />
        </button>
      </form>
    </div>
  );
}

export default CreateArea;
