import React,{useState} from "react";
import AddIcon from '@material-ui/icons/Add';
import Fab from '@material-ui/core/Fab';
import Zoom from '@material-ui/core/Zoom';



function CreateArea(props) {
  var [item, setItem] = useState({
    title:"",
    content:""
  });
  function getItem(event){
    var name = event.target.name;
    var value = event.target.value;

      setItem(
        prevItem => {
          return {
            ...prevItem,
            [name] : value
          } ;
        }
      )
  }

  function preventDefault(event){
    event.preventDefault();
  }

  var [isHidden, setIsHidden] = useState(true);

  var customStyle = {
    visibility: "hidden"
  }

  function showArea(){
    setIsHidden(false);
    customStyle.visibility= "show"
  }

  return (
    <div>
      <form className = "create-note" onSubmit = {preventDefault}>
        <input onClick = {showArea} style = {customStyle} onChange= {getItem}  name="title" placeholder="Title" value={item.title}/>
        <textarea onChange = {getItem} name="content" placeholder="Take a note..." rows="3" value = {item.content} />
        <Zoom in = {!isHidden}>
          <Fab onClick = {()=> props.click(item)}>
            <AddIcon />
          </Fab>
        </Zoom>
      </form>
    </div>
  );
}

export default CreateArea;
